var acceptStr="OK";var cancelStr="CANCEL";var closeStr="CLOSE";function showTooltip(b,d,c,a){if(c=="error"){backgroundColor="rgb(244, 203, 203)";textColor="rgb(50, 50, 50)";borderColor="rgb(185, 10, 10)";imageUrl="images/icons/nuove_error.png"}else{backgroundColor="rgb(255, 245, 189)";textColor="rgb(50, 50, 50)";borderColor="rgb(255, 173, 37)";imageUrl="images/icons/nuove_info.png"}if(a=="left"){cornerTarget="leftMiddle";cornerTooltip="rightMiddle";tipCorner="rightMiddle"}else{if(a=="above"){cornerTarget="topMiddle";cornerTooltip="bottomLeft";tipCorner="bottomLeft"}else{if(a=="below"){cornerTarget="bottomMiddle";cornerTooltip="topLeft";tipCorner="topLeft"}else{cornerTarget="rightMiddle";cornerTooltip="leftMiddle";tipCorner="leftMiddle"}}}htmlStr=getTooltipContent(b,d,imageUrl,"");$(b).qtip({content:{text:htmlStr},position:{corner:{target:cornerTarget,tooltip:cornerTooltip}},show:{delay:0,solo:true,ready:true,when:{event:""},effect:{type:"grow",length:200}},hide:{when:{event:""}},style:{background:backgroundColor,color:textColor,padding:"0px 0px 0px 0px",width:{min:350,max:350},border:{width:0,radius:6,color:borderColor},tip:{corner:tipCorner}}})}function showModalDialog(a,c,b){imageUrl="images/icons/nuove_info.png";htmlStr=getTooltipContent(a,c,imageUrl,b);$(a).qtip({content:{text:htmlStr},position:{target:$(document.body),corner:"center"},show:{delay:0,solo:true,ready:true,when:{event:""}},hide:false,style:{background:"rgb(255, 245, 189)",color:"rgb(50, 50, 50)",padding:"0px 0px 0px 0px",width:{min:350,max:350},border:{width:0,radius:6,color:"rgb(255, 173, 37)"}},api:{beforeShow:function(){$("#modal_blanket").height($(document).height());$("#modal_blanket").fadeIn(200)},beforeDestroy:function(){$("#modal_blanket").fadeOut(200);$("#modal_blanket").height($(document).height())}}})}function getTooltipContent(b,d,a,c){htmlStr="\n";htmlStr+='    <table id="qtip_message_table">\n';htmlStr+="        <thead>\n";htmlStr+="            <tr>\n";htmlStr+='                <th colspan=2><a href="javascript:void(0);" id="qtip_close_button" class="icon_delete" onclick="$(\''+b+"').qtip('destroy');\">"+closeStr+"</a></th>\n";htmlStr+="            </tr>\n";htmlStr+="        </thead>\n";htmlStr+="        <tbody>\n";htmlStr+="            <tr>\n";htmlStr+='                <td><img src="'+a+'"></td>\n';htmlStr+="                <td>"+d+"</td>\n";htmlStr+="            </tr>\n";if(c.length>0){funcStr=c.replace(/%22/g,"'");htmlStr+="            <tr>\n";htmlStr+='                <td colspan=2 class="buttons">';htmlStr+='<a href="javascript:void(0);" class="icon_accept" onclick="$(\''+b+"').qtip('destroy'); "+funcStr+'">'+acceptStr+"</a>";htmlStr+='<a href="javascript:void(0);" class="icon_cancel" onclick="$(\''+b+"').qtip('destroy');\">"+cancelStr+"</a></td>\n";htmlStr+="            </tr>\n"}htmlStr+="        </tbody>\n";htmlStr+="    </table>\n";return htmlStr}function setTranslations(a,b,c){acceptStr=a;cancelStr=b;closeStr=c};
