function showTooltip(b,d,c,a){if(c=="error"){backgroundColor="rgb(244, 203, 203)";textColor="rgb(50, 50, 50)";borderColor="rgb(185, 10, 10)";imageUrl="images/icons/nuove_error.png"}else{backgroundColor="rgb(255, 245, 189)";textColor="rgb(50, 50, 50)";borderColor="rgb(255, 173, 37)";imageUrl="images/icons/nuove_info.png"}if(a=="left"){cornerTarget="leftMiddle";cornerTooltip="rightMiddle";tipCorner="rightMiddle"}else{if(a=="above"){cornerTarget="topMiddle";cornerTooltip="bottomLeft";tipCorner="bottomLeft"}else{if(a=="below"){cornerTarget="bottomMiddle";cornerTooltip="topLeft";tipCorner="topLeft"}else{cornerTarget="rightMiddle";cornerTooltip="leftMiddle";tipCorner="leftMiddle"}}}htmlStr=getTooltipContent(b,d,imageUrl);$(b).qtip({content:{text:htmlStr},position:{corner:{target:cornerTarget,tooltip:cornerTooltip}},show:{delay:0,solo:true,ready:true,when:{event:""},effect:{type:"grow",length:200}},hide:{when:{event:""}},style:{background:backgroundColor,color:textColor,padding:"0px 0px 0px 0px",width:{min:300,max:320},border:{width:0,radius:6,color:borderColor},tip:{corner:tipCorner}}})}function getTooltipContent(b,c,a){htmlStr="\n";htmlStr+='    <table id="qtip_message_table">\n';htmlStr+="        <thead>\n";htmlStr+="            <tr>\n";htmlStr+='                <th colspan=2><a href="javascript:void(0);" class="icon_delete" onclick="$(\''+b+"').qtip('destroy'); return false;\">&nbsp</a></th>\n";htmlStr+="            </tr>\n";htmlStr+="        </thead>\n";htmlStr+="        <tbody>\n";htmlStr+="            <tr>\n";htmlStr+='                <td><img src="'+a+'"></td>\n';htmlStr+="                <td>"+c+"</td>\n";htmlStr+="            </tr>\n";htmlStr+="        </tbody>\n";htmlStr+="    </table>\n";return htmlStr};
